client_script:
// Captcha-related DOM elements
const captchaText = document.getElementById('captcha-text');
const captchaInput = document.getElementById('captcha-input');
const refreshCaptchaButton = document.getElementById('refresh-captcha');
const submitFormButton = document.getElementById('submit-form');

// Function to generate random captcha
function generateCaptcha() {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let captcha = '';
    for (let i = 0; i < 6; i++) {
        captcha += chars.charAt(Math.floor(Math.random() * chars.length)) + ' '; // Adding space for readability
    }
    return captcha.trim(); // Remove the trailing space at the end
}

// Function to refresh captcha
function refreshCaptcha() {
    const newCaptcha = generateCaptcha();
    captchaText.textContent = newCaptcha; // Displaying captcha with spaces
    captchaInput.value = ''; // Clear the captcha input field
}

// Refresh captcha initially
refreshCaptcha();

// Event listener for refresh button click
refreshCaptchaButton.addEventListener('click', refreshCaptcha);

// Event listener for form submission
submitFormButton.addEventListener('click', function() {
    // Get the displayed captcha (with spaces) and remove spaces for comparison
    const displayedCaptcha = captchaText.textContent.replace(/\s+/g, '');
    
    // Verify captcha (compare without spaces)
    if (captchaInput.value === displayedCaptcha) {
        // Optional: Disable form submission button to prevent double submission
        submitFormButton.disabled = true;

        // Save the document using Frappe's web_form.save() method
        frappe.web_form.save({
            callback: function(r) {
                if (!r.exc) {
                    alert('Document saved successfully!');
                    window.location.href = '/desk#Form/YourDoctype/' + r.doc.name;

                    refreshCaptcha();
                    document.getElementById('apply-form').reset();
                    submitFormButton.disabled = false;
                } else {
                    alert('Error: ' + r.exc);
                    submitFormButton.disabled = false;
                }
            },
            error: function(error) {
                console.error('Error saving document:', error);
                alert('An error occurred while saving the document. Please try again.');
                submitFormButton.disabled = false;
            }
        });
        
    } else {
        alert('CAPTCHA verification failed. Please try again.');
        refreshCaptcha();
    }
});

